fn print_lorem_text(text_type: &str, size: usize) -> String {
    if size == 0 {
        return "\"Size\" argument should be greater or equal to one.".to_string();
    }
    if text_type.is_empty() {
        return "Invalid option. The only valid options are 'paragraph', 'sentence' and 'word'."
            .to_string();
    }

    let return_val: Vec<&str>;
    let matcher: &str;
    let mut indexing_range: usize = text_type.len();

    let i_bool = (&text_type[text_type.len() - 1..].to_lowercase() == "s") && (text_type.len() > 1);
    if i_bool {
        indexing_range -= 1
    };

    match &text_type.to_lowercase().as_str()[0..indexing_range] {
        "paragraph" | "parag" | "p" => {
            matcher = "\n\n";
            return_val = LOREM_TEXT.split(&matcher).collect()
        }
        "sentence" | "s" => {
            matcher = ".";
            return_val = LOREM_TEXT.split(&matcher).collect()
        }
        "word" | "w" => {
            matcher = " ";
            return_val = LOREM_TEXT.split(&matcher).collect()
        }
        _ => {
            return "Invalid option. The only valid options are 'paragraph', 'sentence' and 'word'.".to_string();
        }
    };

    match return_val.get(0..size) {
        Some(val) => {
            if matcher == "." {
                return val.join(&matcher) + ".";
            }
            return val.join(&matcher);
        }
        None => String::from("Index/range out of bounds."),
    }
}

fn main() {
    let output: String = print_lorem_text("sentences", 5);
    println!("\n{:?}", output);
}
